let datas = JSON.parse(getVar('juBT-datas')), url = datas.pop(), selected = getVar('juBT-selected', '0'),
  data = [{
    url: $('').x5Rule((titles, selected) => {
      let html = `<meta name="viewport" content="width=device-width"><style>button{margin:0.3rem;border:1px none;background:#f4f6f3;border-radius:0.25rem;font-size:1.025rem;color:#747673}#selected,#expand{background:#e6f3e3;color:#38b076}#expand{position:fixed;top:0.25rem;right:1rem;border-style:solid}</style><script>function reset(i){fy_bridge_app.putVar('juBT-selected',i);fy_bridge_app.refreshPage(true)}</script><button id="expand" onclick="fy_bridge_app.refreshX5Desc(Math.abs(document.body.clientHeight-75)<5?'360&&float':'75&&float')">≡</button>`
      titles.forEach((v, i) => html += `<button id="` + (i == selected ? `selected` : ``) + `" onclick="reset(` + i + `);">` + v + `</button>`)
      document.body.innerHTML = ''
      document.write(html)
      let id = setInterval(() => {
        document.body.scrollTop = document.getElementById('selected').offsetTop - 35
        if (Math.abs(document.getElementById('selected').offsetTop - document.body.scrollTop - 35) < 1 || document.getElementById('selected').offsetTop < 35)
          clearInterval(id)
      }, 5)
    }, datas.map((v) => v.title), selected),
    desc: '75&&float', col_type: 'x5_webview_single'
  }, { title: datas[selected].title + ' 🔗', url: url + '#' + datas[selected].url, col_type: 'text_center_1' }]
Array.prototype.push.apply(data, datas[selected].children)
setResult(data)

