if (!getVar('juBT-datas')) {
  // 在这里的fetch中更换发布页的网址，目前测试了上面3个都能用，修改后需要先重启海阔视界
  let url = 'https://cdn.jsdelivr.net/gh/jubtnet/jubt@master/index.html',
    urls = parseDomForArray(fetch(url, {}), 'tbody&&tr')
  for (let i of urls)
    if (parseDomForHtml(i, 'Text').split(' ')[2] == 'No') {
      url = parseDomForHtml(i, 'a&&href') + '/cn/index.html'
      break
    }
  let datas = fetch(url, {})
    .match(/<h4(?:(?!h4).)*h4><div(?:(?!h4).)*div>/g)
    .slice(1).map((v) => ({
      title: parseDomForHtml(v, 'h4&&Text'),
      url: parseDomForHtml(v, 'h4&&i&&id'),
      children: parseDomForArray('<body>' + v + '</body>', 'body&&.row>div').map((v) => ({ title: parseDomForHtml(v, 'a,1&&Text'), url: parseDomForHtml(v, 'div,1&&data-original-title') })),
    })).concat(url)
  putVar('juBT-datas', JSON.stringify(datas))
}

