let tag = [{
    name: "全部",
    id: 0
}];
let iTag = getMyVar(":iTag", "");
if (iTag) {
    iTag = JSON.parse(iTag);
} else {
    try {
        iTag = JSON.parse(fetch(hUrl, {
            headers: {
                'User-Agent': 'okhttp/3.12.1'
            }
        })).rescont;
        putMyVar(":iTag", JSON.stringify(iTag));
    } catch (e) {
        iTag = [];
    }
}
tag = tag.concat(iTag);

let tagi = Number(getMyVar(":tagi", "0"));
let tTagId = tag[tagi].id;
const layout = [];
if (MY_PAGE === 1) {
    layout.push({
        title: "站点：",
        url: "hiker://empty",
        col_type: "scroll_button"
    });
    urlMap.forEach((item, i) => {
        layout.push({
            title: mapi === i ? "““””" + item.name.fontcolor("#ff01bfa") + "" : item.name,
            url: $("#noLoading#").lazyRule((i) => {
                putMyVar(":mapi", "" + i);
                clearMyVar(":iTag");
                clearMyVar(":tagi");
                refreshPage();
                return "hiker://empty";
            }, i),
            col_type: "scroll_button"
        });
    });
    layout.push({
        col_type: "blank_block"
    });
    layout.push({
        title: "分类：",
        url: "hiker://empty",
        col_type: "scroll_button"
    });
    tag.forEach((item, i) => {
        layout.push({
            title: tagi === i ? "‘‘" + item.name + "’’" : item.name,
            url: $("#noLoading#").lazyRule((i) => {
                putMyVar(":tagi", "" + i);
                refreshPage();
                return "hiker://empty";
            }, i),
            col_type: "scroll_button"
        });
    });
    layout.push({
        url: '"hiker://search?s="+input',
        desc: "搜你想要的......",
        title: "搜索",
        col_type: "input",
        extra: {
            rules: $.toString((urlMap, mapi) => {
                let arr1 = [];
                urlMap.unshift(urlMap.splice(mapi, 1)[0]);
                for (let i = 0; i < urlMap.length; i++) {
                    arr1.push({
                        "title": urlMap[i].name,
                        "search_url": "hiker://empty?searchTerms=**&page=fypage&index=" + i,
                        "searchFind": "js: $.require('hiker://page/Search.view?rule='+MY_RULE._title);"
                    });
                }
                return JSON.stringify(arr1);
            }, urlMap, mapi),
            defaultValue: getMyVar("searchKey", ""),
            onChange: "putMyVar('searchKey',input)"
        }
    });
}

