                refreshPage();
                return "hiker://empty"
            }, source.name)
        })
    }

    data.push({
        col_type: 'blank_block'
    })

    for (source of config.data) {
        if (source.name == select) {
            try {
                let items = $.require('hiker://page/renderData').renderLiveData(source.url);
                let groups = Object.keys(items);
                //groups.push(groups.splice(groups.indexOf(0), 1)[0]);
                let selectGroup = getItem('selectGroup', groups[0]);
                if (selectGroup == "" || !groups.includes(selectGroup)) selectGroup = groups[0];
                groups.forEach((group) => {
                    if (group) {
                        data.push({
                            title: group == selectGroup ? "““””<span style='color: #19B89D'>" + group + "</span>" : group,
                            url: $('#noLoading#').lazyRule((group) => {
