                            col_type: 'scroll_button',
                            extra: {
                                lineVisible: false
                            }
                        })
                    }
                })

                for (let title in items[selectGroup]) {
                    data.push({
                        title: title,
                        url: $().lazyRule((sourceurl,selectGroup,title) => {
                            let items = $.require('hiker://page/renderData').renderLiveData(sourceurl);
                            let surls = items[selectGroup][title].url;
                            let sheaders = items[selectGroup][title].headers;
                            for (let groupi in items){
                                let group = items[groupi];
                                if(!group) continue;
                                for (let ititle in group){
                                    if(ititle == title && !!group[ititle]){
                                        let urls = group[ititle].url;
                                        let headers = group[ititle].headers;
                                       surls = surls.concat(urls);
                                       sheaders = sheaders.concat(headers);
                                    }
                                }
                            }
                            surls = Array.from(new Set(surls));
                            sheaders = Array.from(new Set(sheaders));
                            return JSON.stringify({
                                urls: surls,
                                headers: sheaders
                            })
                        },source.url,selectGroup,title),
