if (urls) {   
    if (Date.now() - Date.parse(urls[0]) > 24*60*60*1000 )
        (new java.lang.Thread(function() {
            if (requestData()) {
                confirm({
                    title: '提示',
                    content: '更新地址成功'
                })
                refreshPage(false)
            } else {
                urls[0] = new Date().toISOString()
                saveFile('urls.json', JSON.stringify(urls), 0)
            }
        })).start()
} else if (!requestData()) {
    confirm({
        title: '提示',
        content: '获取数据失败，请刷新重试'
    })
}

