     var pic = parseDomForHtml(html, pic_rule)
     var list = parseDomForArray(html, list_rule)
      //log({list:list})
      for (var key in list) {
                var pic = parseDomForHtml(list[key], pic_rule)
                //log({pic:pic});
                pics.push(pic);
            }
    for (x in pics) {
        d.push({
            pic_url: pics[x],
            url: pics[x],
            col_type: "pic_3"
        });
