//关闭自定义分类？
const defaultDisTabModify = true;
const QING_TITLE = getMyVar('myCollection'),
    newWindow = readFile('newWindow'),
    typeShow = getMyVar('typeShow', '');
let disTabModify = readFile('disTabModify');
if(disTabModify===''){
    saveFile('disTabModify', defaultDisTabModify+'');
    disTabModify = defaultDisTabModify;
}else if(/true|false/.test(disTabModify)){
    disTabModify = JSON.parse(disTabModify);
}
//log('关闭自定义分类？'+disTabModify);
const {color,small,unique2} = $.require("hiker://page/tools?rule="+ QING_TITLE);

let el = [{
        title: ' ' + QING_TITLE.bold() + ' &nbsp &nbsp ' + '⚙ 轻合集设置 ⚙'.small(),
        url: 'hiker://page/Config?rule=' + QING_TITLE + '&#noRecordHistory#',
        img: MY_RULE.icon || 'hiker://images/icon1',
        col_type: 'avatar'
    }, {
        url: '"hiker://search?s="+input',
        desc: '搜你想要的...',
        title: '搜索',
        col_type: 'input',
        extra: {
            rules: $.toString((QING_TITLE, typeShow,disTabModify) => {
                let rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)();
                if (disTabModify&&typeShow) {
                    rules = rules.filter((v) => v.type === typeShow);
                }else if(!disTabModify){
                    typeNow = typeShow || '全部';
                    if(typeNow==='其他'){
                        rules = rules.filter((v) => !v.sort_url);
                    }else if(typeNow!=='全部'){
                        rules = rules.filter((v) => v.sort_url===typeNow);
                    }
                }
                return JSON.stringify(rules)
            }, QING_TITLE, typeShow,disTabModify),
            defaultValue: getMyVar('searchKey', ''),
            onChange: "putMyVar('searchKey',input)"
        }
    }],
    rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)();

if(disTabModify) {
    types = { '': '全部', 'all': '聚合', 'video': '视频', 'music': '音频', 'live': '直播', 'cartoon': '漫画', 'read': '阅读', 'picture': '图集', 'news': '资讯', 'tool': '工具', 'other': '其它' };
    for (let k in types) {
        if (!k || rules.find((v) => v.type === k)) {
            let title = '““””' + (k === typeShow ? types[k].bold().fontcolor('#12b668') : types[k]);
            let cnt = rules.filter(it => !k || it.type === k).length;
            el.push({
                title: title + ' ' + color(cnt, '#ff7000'),
                url: k === typeShow ? 'hiker://empty' : $('#noLoading#').lazyRule((k) => {
                    putMyVar('typeShow', k)
                    refreshPage(false)
                    return 'hiker://empty'
                }, k),
                col_type: 'scroll_button'
            })
        }
    }

    if (typeShow) {
        rules = rules.filter((v) => v.type === typeShow);
    }
}else{
    types = rules.map(it => it.sort_url || "其他");
    types.unshift('全部');
    types = unique2(types);
    typeNow = typeShow || '全部';
    for (let k in types) {
        let its = types[k];
        let title = '““””' + (its === typeNow ? its.bold().fontcolor('#12b668') : its);
        let cnt;
        if (its === '全部') {
            cnt = rules.length
        } else if (its === '其他') {
            cnt = rules.filter(it => !it.sort_url).length;
        } else {
            cnt = rules.filter(it => it.sort_url === its).length;
        }
        el.push({
            title: title + ' ' + color(cnt, '#ff7000'),
            url: its === typeNow ? 'hiker://empty' : $('#noLoading#').lazyRule((its) => {
                putMyVar('typeShow', its);
                refreshPage(false)
                return 'hiker://empty'
            }, its),
            col_type: 'scroll_button'
        })
    }
    if(typeNow==='其他'){
        rules = rules.filter((v) => !v.sort_url);
    }else if(typeNow!=='全部'){
        rules = rules.filter((v) => v.sort_url===typeNow);
    }
}
eval(JSON.parse(fetch('hiker://page/urlParse?rule=' + QING_TITLE)).rule);
rules.forEach((v) => {
    let urlParam = v.url.split(';'),
        ruleTmp = {
            title: v.title, url: urlParam.shift(),
            class_name: v.class_name, class_url: v.class_url,
            area_name: v.area_name, area_url: v.area_url,
            year_name: v.year_name, year_url: v.year_url,
            sort_name: v.sort_name, sort_url: v.sort_url
        },
        fypage = ruleTmp.url.includes('fypage') ? 'fypage' : '1',
        domain = (/(https?:\/\/.+?)(?:\/|$)/i.exec(ruleTmp.url) || [, 'hiker://empty'])[1],
        d = {
            title: v.title,
            url: 'hiker://page/frameLoad',
            img: v.icon || 'hiker://images/icon1',
            extra: {
                url: domain + '/#noHistory##noRecordHistory####' + JSON.stringify({
                    RULE: base64Encode(JSON.stringify(ruleTmp)),
                    pageNum: fypage,
                    QING_TITLE: getMyVar('myCollection')
                }) + '.js:eval(JSON.parse(fetch("hiker://page/urlParse？？rule=' + QING_TITLE + '")).rule)；；indexUrl(input);' + paramHandle(urlParam, v.ua),
                RULE: { title: v.title },
                isIndex: true
            }
        }
    if (newWindow) Object.assign(d.extra, {
        newWindow: true,
        windowId: QING_TITLE + '_' + d.title
    })
    el.push(d)
});
setResult(el)

