}

function cssTagReplacement2(context, searchValue, replaceValue) {
    let matchArray = context.match(searchValue);
    let returnValue = context;
    if (matchArray != null) {
        for (let i = 0; i < matchArray.length; i++) {
            let item = matchArray[i];
            /<(div|p|strong)>.*?<\/(div|p|strong)>/.test(item);
            let css = RegExp.$2;
