        let vcode = getVCode2(MY_HOME + '/index.php/verify/index.html?', JSON.stringify(headers), 'num');
        fetch(MY_HOME + html.match(/\/index.php.*?verify=/)[0] + JSON.parse(vcode).ret, {
            headers: headers,
            method: 'POST'
        });
        reload = true;
    }
    return reload;
}

function test(url, html, d, old, key, rule) {
    if (/5秒|频繁/.test(html)) {
