function isMovie(it) {
    if (it == null || it.text == null) {
        return false;
    }
    let tit = it.title || "";
    it = it.text || "";
    if (it == "" || it.length > 8) {
        return false;
    }
    if (tit != "" && !tit.includes(it)) {
        return false;
    }
    return it.match(/原画|备用|蓝光|超清|正片|韩版|4K|4k|1080P|720P|TC|HD|BD/)
}

function notChapter(it) {
    if (it == null || it.text == null) {
        return true;
    }
    return it.text.match(/[0-9]\.[0-9]分/);
}

function isChapter(it, pre, next) {
    if (notChapter(it)) {
        //优先排除
        return false;
    }
    //判断是不是电影
    if (isMovie(it)) {
        return true;
    }
    return isChapter0(it, pre) || isChapter0(it, next);
}

function getChapterNum(it) {
    if (it == null || it.text == null) {
        return -1;
    }
    it = it.text || "";
    if (it == "") {
        return -1;
    }
    it = clearText(it);
    it = parseInt(it);
    if (isNaN(it)) {
        return -1;
    }
    return it;
}

function isChapter0(it, brother) {
    /*if (debug) {
        log({
            it: it,
            brother: brother
        });
    }*/
    it = getChapterNum(it);
    //if (debug) log(it);
    if (it < 0) {
        return false;
    }
    brother = getChapterNum(brother);
    //if (debug) log(brother);
    if (brother < 0) {
        return false;
    }
    return it - brother < 2 && it - brother > -2;
}

