"},{"col_type":"movie_3","name":"函数工具","path":"funcTools","rule":"let { QING_TITLE, MY_RULE, isIndex } = $.importParam
if (!MY_RULE) MY_RULE = {}

function runCode(rule) {
    try {
        let [input, code] = rule.split('.js:')
        return code ? eval(code) : rule
    } catch (e) { return rule }
}
// 首页链接替换分类
function indexUrl(param) {
    MY_RULE = JSON.parse(base64Decode(MY_RULE))
    let pageNum = parseInt(param.split('###')[1]),
        url = MY_RULE.url,
        { fyAll, tabHeader } = $.require('hiker://page/ClassTab?rule=' + QING_TITLE, Object.assign({ QING_TITLE: QING_TITLE }, MY_RULE))

    url = tabHeader.setUrl(fyAll ? url.replace(/fyAll/g, '$${fyAll}') : url.replace(/fy(class|area|year|sort)/g, '$${fy$1}'))
    url = url.replace(/fypage(?:@(-?\d+)@)?(?:\*(\d+)@)?/g, (_, start, space) => parseInt(start || 0) + 1 + (pageNum - 1) * parseInt(space || 1))
    url = /^([\s\S]*?)(?:\[firstPage=([\s\S]*?)\])?(?:(\.js:[\s\S]*?))?$/.exec(url)
    if (pageNum == 1 && url[2]) url[1] = url[2]
    if (url[3] && !url[1].includes('.js:')) url[1] += url[3]
    return runCode(url[1])
}

function paramHandle(urlParam, ua, urlTmp) {
    let charset, paramTmp
    try { // 链接的编码、UA和Referer应该是首先继承首页链接
        [, , charset, paramTmp] = urlTmp.split(';')
        paramTmp = paramTmp.match(/^{(.*)}$/)[1].split('&&')
    } catch (e) { paramTmp = [] }
    let UserAgent = paramTmp.find((v) => v.startsWith('User-Agent@')),
        Referer = paramTmp.find((v) => v.startsWith('Referer@'))
    urlParam[0] = urlParam[0] || 'GET'
    urlParam[1] = urlParam[1] || charset || 'UTF-8'
    try {
        urlParam[2] = urlParam[2].match(/^{(.*)}$/)[1].split('&&')
    } catch (e) { urlParam[2] = [] }
    // 添加全局UA和上级Referer
    if (!urlParam[2].some((v) => v.startsWith('User-Agent@'))) {
        if (UserAgent) urlParam[2].push(UserAgent)
        else if (ua == 'pc')
            urlParam[2].push('User-Agent@' + PC_UA.replace(/;/g, '；；').replace(/\?/, '？？'))
        else if (ua == 'mobile')
            urlParam[2].push('User-Agent@' + MOBILE_UA.replace(/;/g, '；；').replace(/\?/, '？？'))
    }
    if (Referer && !urlParam[2].some((v) => v.startsWith('Referer@')))
        urlParam[2].push(Referer)
    urlParam[2] = '{' + urlParam[2].join('&&') + '}'
    return urlParam.join(';')
}
// 预处理并初始化config
function runPreRule(rule) {
    if ((typeof MY_PAGE != 'undefined' &&  MY_PAGE == 1) &&
         (isIndex || !getMyVar('$preRule_' + MY_RULE.title))) {
        eval(rule)
        putMyVar('$preRule_' + MY_RULE.title, 'T')
    }
    let _cfg = getMyVar('initConfig', '{}')
    if (_cfg && _cfg.length > 0) {
        config = JSON.parse(_cfg)
    }
}
// 正文解析通用变量
function genMyRule() {
    let RULE = Object.assign({}, MY_RULE)
    delete RULE.last_chapter_rule
    delete RULE.preRule
    delete RULE.pageList
    delete RULE.pages
    return RULE
}

function genExUrl(url) {
    url = url.split(';')
    return url.shift() + ';' + paramHandle(url, MY_RULE.ua, MY_RULE.url)
}

hijackLazyRule = $.toString((QING_TITLE, MY_PARAMS) => {
    if (!MY_RULE) MY_RULE = {}
    MY_PARAMS = JSON.parse(base64Decode(MY_PARAMS))
    eval(JSON.parse(fetch('hiker://page/hijackEnv?rule=' + QING_TITLE)).rule)
    var {
        paramHandle,
        genExUrl,
        hijackLazyRule,
        hijackEl
    } = $.require('hiker://page/funcTools?rule=' + QING_TITLE)
}, QING_TITLE, base64Encode(JSON.stringify({ url: MY_RULE.url, RULE: base64Encode(JSON.stringify(genMyRule())), isIndex: isIndex })))

function hijackEl(el) {
    if(!el || typeof el != 'object') return
    if (!el.extra) el.extra = {}
    el.col_type = el.col_type || MY_RULE.col_type
    let NOW_RULE = Object.assign(genMyRule(), { params: el.extra })

    if (typeof el.url != 'string' ||
        ['x5_webview_single', 'input'].some((v) => el.col_type == v) ||
        ['海阔视界', 'javascript:'].some((v) => el.url.startsWith(v)) ||
        ['rule', 'pics', 'toast', 'copy', 'editFile', 'x5', 'x5WebView', 'x5Play', 'web', 'x5Rule', 'webRule',
            'download', 'share', 'fileSelect', 'video'].some((v) => el.url.startsWith(v + '://'))) {
        return el
    } else if (el.url.startsWith('input://')) {
        let para = JSON.parse(el.url.slice(8))
        if (para.js) para.js = hijackLazyRule + ';' + para.js
        el.url = 'input://' + JSON.stringify(para)
        return el
    } else if (el.url.startsWith('confirm://')) {
        let reIndex = el.url.indexOf('.js:')
        if (reIndex >= 0) el.url = el.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + el.url.slice(reIndex + 4)
        return el
    } else if (el.url.startsWith('select://')) {
        let para = JSON.parse(el.url.slice(9))
        if (para.js) para.js = hijackLazyRule + ';' + para.js
        el.url = 'select://' + JSON.stringify(para)
        return el
    } else if (el.url.includes('@rule=')) {
        let [_, url, rule] = el.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
        el.url = url
        NOW_RULE.detail_find_rule = rule
        NOW_RULE.detail_col_type = MY_RULE.col_type
    } else if (el.url.startsWith('hiker://page/')) {
        if (el.url.includes('rule=') || el.extra.rule)
            return el
        let [_, path, flag, params] = el.url.match(/^hiker:\/\/page\/(.+?)(#.*?)?(?:\?(.*))?$/),
            subPage = MY_RULE.pageList.find((v) => v.path == path),
            subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
        el.url = (subUrl ? subUrl.slice(4).replace(/？？/g, '?').replace(/＆＆/g, '&') : (el.extra || {}).url) || 'hiker://empty' + flag + '?' + (params || '')
        NOW_RULE.detail_find_rule = subPage.rule
        NOW_RULE.detail_col_type = subPage.col_type
    } else if (el.url.includes('@lazyRule=')) {
        let reIndex = el.url.indexOf('.js:')
        if (reIndex >= 0) el.url = el.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + el.url.slice(reIndex + 4)
        return el
    } else if (isIndex) {
        if (!MY_RULE.detail_find_rule || /hiker:\/\/(?!empty.+$)/.test(el.url)) return el
    } else {
        return el
    }
    el.extra = Object.assign({}, el.extra, {
        url: genExUrl(el.url),
        RULE: base64Encode(JSON.stringify(NOW_RULE)),
        pageTitle: isIndex && el.title
    })
    el.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
    return el
}
$.exports = {
    runCode: runCode,
    indexUrl: indexUrl,
    paramHandle: paramHandle,
    runPreRule: runPreRule,
    genMyRule: genMyRule,
    genExUrl: genExUrl,
    hijackLazyRule: hijackLazyRule,
    hijackEl: hijackEl
}
"}]

